mapper.update:

UPDATE basketlist_his
SET status  = 9,
cancel_date = sysdate
WHERE user_id = #{user_id}
and seq_no = #{seq_no}


UPDATE basketlist_his
SET status = 9
WHERE user_id = #{user_id}
  AND goods_code = #{in_goods_code}

UPDATE interestrate_his
SET interest_rate = #{tr_price}
WHERE goods_code = #{goods_code}
and period_mm = (select period_mm  from goods_mas where goods_code = #{goods_code})

UPDATE baseprice_his
SET trbase_price = #{tr_price},
   trxbase_price = #{tr_price}
WHERE goods_code = #{goods_code}
    and base_date = to_char(sysdate,'yyyymmdd')
